resources.string:
public class MainActivity extends AppCompatActivity {
 @Override
 protected void onCreate(@Nullable Bundle savedInstanceState) {
 super.onCreate(savedInstanceState);
 setContentView(R.layout.activity_main);
 RecyclerView recyclerView=findViewById(R.id.rview);
 List<Users> users=Users.getUsers();
 recyclerView.setLayoutManager(new LinearLayoutManager(getApplicationContext()));
 recyclerView.setItemAnimator(new DefaultItemAnimator());
 CustomRecyclerViewAdapter customRecyclerViewAdapter=new CustomRecyclerViewAdapter(getApplicationContext());
 recyclerView.setAdapter(customRecyclerViewAdapter);
 customRecyclerViewAdapter.updateData(users);
 }
 }

public class Users {
 private String name;
 private String email;
 private String phoneno;

 public Users(String name, String email, String phoneno) {
 this.name = name;
 this.email = email;
 this.phoneno = phoneno;
 }

 public String getName() {

 return name;
 }

 public String getEmail() {
 return email;
 }

 public String getPhoneno() {
 return phoneno;
 }

 public static List<Users> getUsers(){
 List<Users> users=new ArrayList<>();
 for(int i=0;i<100;i++){
 String name="User"+(i+1);
 String email="user@xyz.com"+(i+1);
 String phone="123456789";
 Users user=new Users(name,email,phone);
 users.add(user);

 }
 return users;
 }
 }

public class CustomRecyclerViewAdapter extends RecyclerView.Adapter<CustomRecyclerViewAdapter.CustomHolder> {

 private List<Users> usersList;

 private LayoutInflater layoutInflater;



 public CustomRecyclerViewAdapter(Context context) {

 layoutInflater=LayoutInflater.from(context);

 usersList=new ArrayList<>();

 }



 @NonNull

 @Override

 public CustomHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {

 View view=layoutInflater.inflate(R.layout.items,parent,false);

 return new CustomHolder(view);

 }



 @Override

 public void onBindViewHolder(@NonNull CustomHolder holder, int position) {

 Users users=usersList.get(position);

 holder.email.setText(users.getEmail());

 holder.phone.setText(users.getPhoneno());

 holder.name.setText(users.getName());



 }

 public void updateData(List<Users> users){

 usersList=users;

 notifyDataSetChanged();

 }



 @Override

 public int getItemCount() {

 return usersList==null?0:usersList.size();

 }



 class CustomHolder extends RecyclerView.ViewHolder {

 TextView name,phone,email;

 CustomHolder(View itemView) {

 super(itemView);

 name=itemView.findViewById(R.id.rname);

 phone=itemView.findViewById(R.id.rphone);

 email=itemView.findViewById(R.id.remail);

 }

 }

 }
